import { Reducer } from 'redux';
import produce from 'immer';

import { LeaderboardAction } from '';
import { ILeaderboard } from 'types';
import * as actionTypes from 'actions/action-types';
import { HotelListAction } from '../../action';


const initialState = {
  activeLeaderboardTab: 'locations'
};

export const leaderboardReducer= (prevState: ILeaderboard = initialState, action: HotelListAction) => {
  switch (action.type) {
    case actionTypes.ACTIVE_LEADERBOARD_TAB:
      return produce(prevState, (nextState) => {
        nextState.activeLeaderboardTab = action.payload;
      });
    default: return prevState;
  }
};
