import { Button, Container, Dropdown, Form } from "react-bootstrap"

import "../../assets/common.css"
import { useEffect, useState } from "react"
import { useDispatch, useSelector } from "react-redux"
import { hotelListRequest } from "../../action"
import { IHotelListResponse, IStore } from "../../utils/types"
import DatePicker from "react-datepicker";

import "react-datepicker/dist/react-datepicker.css";
const Dashboard = () => {

  const [location, setLocation] = useState<IHotelListResponse | null>(null);

  const [chekIn, setCheckIn] = useState<Date | null>(null);
  const [chekOut, setChekOut] = useState<Date | null>(null);
  const [guestCount, setGuestCount] = useState<number>(0);
  const [roomCount, setRoom] = useState<number>(0);
  console.log("ðŸš€ ~ Dashboard ~ roomCount:", roomCount, guestCount)


  const hotelListRespose = useSelector((state: IStore) => state.common.hotelListRespose)
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(hotelListRequest())
  }, []);

  const resetStates=()=>{
    setChekOut(null);
    setCheckIn(null)
    setGuestCount(0);
    setRoom(0)
    setLocation(null)
  }
  const bookHotel = () => {
    console.log('clicked')
    const isError  = [location, chekIn, chekOut, guestCount, roomCount].filter
    resetStates()
  }

  return (
    <Container fluid className="container-class">
      <div className="child-container">
        <div className="child-ele">
          <>Select location</>
          <Dropdown className="p-10">
            <Dropdown.Toggle variant="success" id="dropdown-basic">
              {location ? location?.hotel_name : 'Location'}
            </Dropdown.Toggle>
            <Dropdown.Menu>
              {hotelListRespose.map((it, index) => {
                return (
                  <Dropdown.Item
                    key={index}
                    active={it.id === location?.id}
                    onClick={() => {
                      setLocation(it);
                    }}
                  >
                    {it.hotel_name}
                  </Dropdown.Item>)
              })}
            </Dropdown.Menu>
          </Dropdown>
        </div>

        <div className="child-ele">
          <>CheckIn Date</>
          <DatePicker selected={chekIn} placeholderText="MM/DD/YYYY" onChange={(date: Date | null) => setCheckIn(date)} />
        </div>
        <div className="child-ele">
          <>CheckOut Date</>
          <DatePicker selected={chekOut} placeholderText="MM/DD/YYYY" onChange={(date: Date | null) => setChekOut(date)} />
        </div>


        <div className="child-ele">
          <>No of Guests</>
          <Form>
            <Form.Control type="text" onChange={(e) => {
              const value = e.target.value;

              if (/^\d*$/.test(value)) {
                if (Number(value) < 13) {
                  setGuestCount(Number(value))
                }
              }

            }} value={guestCount} />
          </Form>
          <>Max 12 Guest</>
        </div>

        <div className="child-ele">
          <>No of Rooms {location?.available_rooms || 0}</>
          <Form>
            <Form.Control type="text" onChange={(e) => {
              console.log('triggered')
              const value = e.target.value;
              if (/^\d*$/.test(value)) {
                if (Number(value) < 5) {
                  setRoom(Number(value))
                }
              }
            }} value={roomCount} />
          </Form>
          <>Max 4 Rooms</>

        </div>
        <Button onClick={bookHotel}> Book Hotel</Button>
      </div>
    </Container>
  )
}

export default Dashboard
