import { Button, Container, Dropdown } from "react-bootstrap"

import "../../assets/common.css"
import { useEffect, useState } from "react"
import { useDispatch, useSelector } from "react-redux"
import { hotelListRequest } from "../../action"
import { IHotelListResponse, IStore } from "../../utils/types"
import DatePicker from "react-datepicker";

import "react-datepicker/dist/react-datepicker.css";
const Dashboard = () => {

  const [location, setlocation] = useState<IHotelListResponse | null>(null);

  const [chekIn, setCheckIn] = useState<Date | null>();
  const [chekOut, setChekOut] = useState<Date | null>();


  const hotelListRespose = useSelector((state: IStore) => state.common.hotelListRespose)
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(hotelListRequest())
  }, []);

  return (
    <Container fluid className="container-class">
      <div className="child-container">
        <div className="child-ele">
          <>Select location</>
          <Dropdown className="p-10">
            <Dropdown.Toggle variant="success" id="dropdown-basic">
              {location ? location?.hotel_name : 'Location'}
            </Dropdown.Toggle>
            <Dropdown.Menu>
              {hotelListRespose.map((it, index) => {
                return (
                  <Dropdown.Item
                    key={index}
                    active={it.id === location?.id}
                    onClick={() => {
                      setlocation(it);
                    }}
                  >
                    {it.hotel_name}
                  </Dropdown.Item>)
              })}
            </Dropdown.Menu>
          </Dropdown>
        </div>

        <div  className="child-ele">
        <>Select CheckIn Date</>
          <DatePicker selected={chekIn} onChange={(date: Date | null) => setCheckIn(date)} />
        </div>
        <div  className="child-ele">
        <>Select CheckOu Date</>
          <DatePicker selected={chekOut} onChange={(date: Date | null) => setChekOut(date)} />
        </div>

        <Button onClick={() => {
          // dispatch()
        }}> Search</Button>
      </div>
    </Container>
  )
}

export default Dashboard
