import { AnyAction, applyMiddleware, combineReducers, createStore, Dispatch, Middleware, Store } from "redux";
import { commonReducer } from "./dashboard.ts";
import { IStore } from "../utils/types/index.js";
import createSagaMiddleware, { SagaMiddleware } from "redux-saga";


export const appReducer = combineReducers<IStore>({
  common: commonReducer
});

const rootReducer = (state: IStore | undefined, action: any) => {
  return appReducer(state, action);
};

const sagaMiddleWare: SagaMiddleware<{}> = createSagaMiddleware();
const middleWare: Middleware[] = [sagaMiddleWare];

export const store: Store<IStore, AnyAction> & {
  dispatch: Dispatch<any>;
} = createStore(rootReducer, applyMiddleware(...middleWare));
